DROP TABLE IF EXISTS issue;
DROP TABLE IF EXISTS book_tag;
DROP TABLE IF EXISTS tag;
DROP TABLE IF EXISTS rating;
DROP TABLE IF EXISTS review;
DROP TABLE IF EXISTS favourite;
DROP TABLE IF EXISTS book;
DROP TABLE IF EXISTS user;
DROP TABLE IF EXISTS game_history;

CREATE TABLE book
(
    id          INT PRIMARY KEY AUTO_INCREMENT,
    name        VARCHAR(100)  NULL,
    author      VARCHAR(100)  NULL,
    description VARCHAR(5000) NULL,
    time_added  TIMESTAMP     NULL,
    copies      INT           NULL
);

CREATE TABLE user
(
    id        INT PRIMARY KEY AUTO_INCREMENT,
    username  VARCHAR(20) UNIQUE,
    password  VARCHAR(20) NULL,
    type      INT         NULL,
    firstName VARCHAR(20) NULL,
    lastName  VARCHAR(20) NULL,
    email     VARCHAR(20) NULL,
    registration_date TIMESTAMP,
    card_view TINYINT(1)  NOT NULL DEFAULT 0
);

-- TODO: Remove the username field
CREATE TABLE issue
(
    id         INT PRIMARY KEY AUTO_INCREMENT,
    start_date TIMESTAMP,
    duration   INT,
    user_id    INT,
    username   VARCHAR(20),
    book_id    INT,
    status     VARCHAR(30),
    end_date   TIMESTAMP NULL,
    FOREIGN KEY (book_id) REFERENCES book (id) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (user_id) REFERENCES user (id) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (username) REFERENCES user (username) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE tag
(
    name           VARCHAR(100) PRIMARY KEY,
    bg_color_hex   VARCHAR(10),
    text_color_hex VARCHAR(10)
);

CREATE TABLE book_tag
(
    book_id INT,
    tag     VARCHAR(100),
    FOREIGN KEY (book_id) REFERENCES book (id) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (tag) REFERENCES tag (name) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE rating
(
    user_id    INT,
    book_id    INT,
    rating     INT,
    time_added TIMESTAMP,

    FOREIGN KEY (book_id) REFERENCES book (id) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (user_id) REFERENCES user (id) ON DELETE CASCADE ON UPDATE CASCADE,
    UNIQUE KEY unique_user_book (user_id, book_id)
);

CREATE TABLE review
(
    book_id    INT,
    user_id    INT,
    review     VARCHAR(1000),
    time_added TIMESTAMP,

    FOREIGN KEY (book_id) REFERENCES book (id) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (user_id) REFERENCES user (id) ON DELETE CASCADE ON UPDATE CASCADE,
    UNIQUE KEY unique_user_book (user_id, book_id)
);

CREATE TABLE favourite
(
    book_id    INT,
    user_id    INT,
    time_added TIMESTAMP,

    FOREIGN KEY (book_id) REFERENCES book (id) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (user_id) REFERENCES user (id) ON DELETE CASCADE ON UPDATE CASCADE,
    UNIQUE KEY unique_user_book (user_id, book_id)
);

CREATE TABLE game_history (
    id INT PRIMARY KEY AUTO_INCREMENT,
    user_id INT NOT NULL,
    score INT NOT NULL,
    question_answered INT NOT NULL,
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP NOT NULL
);



